package 程序控制结构;

public class 程序的结构设计 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4,b=5;
		
		System.out.println("a="+a+" ,b="+b);
		switch(a)
		{
		case 6:
			System.out.println("666");
			break;
		case 5:
			System.out.println("555");
			break;
		default:
			System.out.println("default");
		}
	}

}
